hudson.tasks.Shell.command:
export jname=${JOB_NAME}
echo "start build i ${jname}"
sudo jexec ${jname} sh -c "cd /usr/src && make -j 4 buildworld" sudo jexec ${jname} sh -c "cd /usr/src && make -j 4 buildkernel"

export jname=${JOB_NAME}

echo "clean up jail ${jname}"

sudo jail -r ${jname}
sudo umount ${jname}/usr/src
sudo umount ${jname}/dev
sudo rm -fr ${jname}
#sudo chflags -R noschg ${jname}
#sudo rm -fr ${jname}
