hudson.tasks.Shell.command:
# for debugging purposes, show the environment env
cd ${WORKSPACE}
TMPFILE=`mktemp /tmp/poudriere.XXXXXX`
PACKAGES_TO_BUILD=`svn log -v -r ${SVN_REVISION} --xml http://svn0.us-west.freebsd.org/ports/head | xml sel -t -m "/log/logentry/paths/path[@action!='D']" -v "node()" -n | grep '^\/head' | cut -d'/' -f3-4 | grep '\/' | egrep -E -v '^(Mk|Tools|Templates|Keywords|CVSROOT)' | grep -v Makefile | sort | uniq` echo "${PACKAGES_TO_BUILD}" >> ${TMPFILE}
# from here down we rely on /usr/ports being a symlink to the workspace
# cd /usr/ports/ # make fetchindex
# # really brave, find all the ports that depend on modified ports and add them to the list # TMPFILE2=`mktemp /tmp/index.XXXXXX` # for x in `cat ${TMPFILE}` ; do # cd /usr/ports/${x} # PN=`make -V PKGNAME` # grep ${x} /usr/ports/INDEX-11 ; done | cut -d\| -f 2 | cut -d/ -f 4,5 | sort -u > ${TMPFILE2} # # cat ${TMPFILE2} >> ${TMPFILE}
# if there were no port changes (only things like Mk/* or MOVED, etc.), then bail BUILD_COUNT=`cat ${TMPFILE} | wc -l | xargs` if [ "${BUILD_COUNT}" = "0" ]; then echo "Nothing to build" ; exit 0 ; fi
# actually, really build nice sudo poudriere bulk -N -t -C -B ${BUILD_NUMBER} -f ${TMPFILE} -p default -j headamd64
